  '[Products] loadProducts',
  props<{ page:number; pageSize:number; minRating:number|null; ordering:string|null }>()
);

export const loadProductsSuccess = createAction(
  '[Products] loadProductsSuccess',
  props<{ data:{ count:number; results:Product[] } }>()
);

export const loadProductsFailure = createAction(
  '[Products] loadProductsFailure',
  props<{ error:string }>()
);

// optionnel (TP)
export const loadRating = createAction('[Products] loadRating', props<{ id:number }>());
